// ==UserScript==
// @name         Site sentinel
// @namespace    https://github.com/smkapilgupta
// @version      1.3.12
// @description  Script to monitor a wesite
// @author       Kapil Gupta <smkapilgupta@gmail.com>
// @match        *://*/*
// @grant        window.onurlchange
// @grant        GM_addStyle
// @grant        GM_xmlhttpRequest
// @grant        GM_setValue
// @grant        GM_getValue
// @run-at       document-idle
// @require      http://code.jquery.com/jquery-2.2.4.js
// @updateURL    https://raw.githubusercontent.com/smkapilgupta/siteBlocker2/main/siteSentinel.user.js?v=1
// @downloadURL  https://raw.githubusercontent.com/smkapilgupta/siteBlocker2/main/siteSentinel.user.js?v=1
// @icon         data:image/png;base64,
// ==/UserScript==

const preDefinedSentinels=[{key: "AMZN_STOCK_PRICE", url:"https://www.marketwatch.com/investing/stock/amzn", regex:"(?<=Amazon\.com Inc[^0-9]*U\.S\.: Nasdaq[^0-9]*price[^0-9]*)[0-9]+(\.[0-9]*)?", isOrange:(value)=>value<175, isRed: (value)=>value<165}]
const keys=GM_getValue("keys")
const urls=GM_getValue("urls")
const regexs=GM_getValue("regexs")
const isOrangeFunctions=GM_getValue("isOrangeFunctions")
const isRedFunctions=GM_getValue("isRedFunctions")
let sentinels=preDefinedSentinels
keys?.forEach((key,index)=>{
  sentinels.push({key: key,
                 url: urls[index],
                 regex: regexs[index],
                 isOrange:new Function('return ' + isOrangeFunctions[index])(),
                 isRed:new Function('return ' + isRedFunctions[index])()
                 })
})
if(!keys){
  GM_setValue("keys",[])
  GM_setValue("urls",[])
  GM_setValue("regexs",[])
  GM_setValue("isOrangeFunctions",[])
  GM_setValue("isRedFunctions",[])
}
const lastUpdateDateTimeId="LAST_UPDATED_DATE_TIME"
const orangeColor="#f57e00"
const redColor="#e82e09"
const noColor="#d4d4d4"
const dataRefreshPeriodMinutes=15
const bubbleRefreshPeriodMinutes=1

function createButton(id, buttonType, text, action){
  GM_addStyle("\
    .secondary-script-button {\
      background:rgba(255,255,255,1);\
      font-size:16px;\
      cursor: pointer;\
      border: none;\
      text-align: center;\
      padding: 8px 15px 5px 15px;\
      border: 1px solid #000000;\
      margin-left: 10px;\
      color: black;\
    }\
    .primary-script-button {\
      background:rgba(240,121,2,1);\
      font-size:16px;\
      color: white;\
      cursor: pointer;\
      border: none;\
      text-align: center;\
      padding: 8px 15px 5px 15px;\
      margin-left: 10px;\
    }\
    .symbolic-script-button {\
      background:rgba(255,255,255,1);\
      font-size:15px;\
      cursor: pointer;\
      border: none;\
      text-align: center;\
      padding: 8px 15px 5px 14px;\
      border: 1px solid #CCCCCC;\
      margin-left: 10px;\
      border-radius: 3000px;\
      color: black;\
      height: 40px;\
      width: 40px;\
      font-family: Arial;\
      line-height: 1.6;\
      font-weight: bold;\
    }\
  ")
  const btn=document.createElement("button")
  btn.id=id
  btn.textContent=text
  btn.addEventListener("click",action)
  if(buttonType==="primary")
    btn.classList.add(id,"primary-script-button")
  else if (buttonType==="secondary")
    btn.classList.add(id,"secondary-script-button")
  else if (buttonType==="symbolic")
    btn.classList.add(id,"symbolic-script-button")
  return btn
}

function generateRandomNumber(){
    let uuid=String(crypto.randomUUID())
    var sumOfChars=0;
    for(let i=0;i<uuid.length;i++){
        sumOfChars+=Number(uuid.charCodeAt(i))
    }
    return sumOfChars
}

function createToast(message, toastType, timeoutMs){
  const toastContainerId="toastContainer"+String(generateRandomNumber())
  GM_addStyle("\
  .toastContainer{\
    position:fixed;\
    top:0%;\
    left:50%;\
    padding: 8px 15px 5px 15px;\
    background: white;\
    border 1px solid #d4d4d4;\
    border-radius: 5px;\
    z-index: 100000000000000000000000000000;\
    box-shadow: 0px 0px 5px #a0a0a0;\
    transform: translate(-50%,0%);\
    float: left;\
    display: inline;\
    white-space: pre;\
    text-align: left;\
    font-family: Arial;\
    line-height: 1.4;\
  font-weight: normal;\
  }\
  #toastBody{\
    font-size: 15px;\
    display:inline-block;\
    color: black;\
  }\
  ")
  const container=document.createElement("div")
  container.classList.add("toastContainer")
  container.id=toastContainerId

  const body=document.createElement("div")
  body.textContent=message
  body.id="toastBody"

  const closeButton=createButton("closeToast","symbolic","X",()=>{
    removeIfPresent(document.body,toastContainerId,undefined)
  })

  prepend(container,body,closeButton)
  prepend(document.body,container)

  setTimeout(()=>{
    removeIfPresent(document.body,toastContainerId,undefined)
  },timeoutMs?timeoutMs:20000)
}

function prepend(wrapper, ...elements){
  if(!wrapper)
    return
	elements.reverse()
	elements.forEach(element=>{
    wrapper.insertBefore(element,wrapper.firstChild)
	})
}

function removeIfPresent(wrapper, elementId, elementClass){
	if(!wrapper)
		return
	if(elementId&&wrapper.querySelector("#"+elementId))
		wrapper.querySelector("#"+elementId).remove()
  if(elementClass&&wrapper.querySelector(elementClass))
    wrapper.querySelector(elementClass).remove()
}

function hslToRgb(hsl) {
	const h = hsl[0] / 360;
	const s = hsl[1] / 100;
	const l = hsl[2] / 100;
	let t2;
	let t3;
	let val;

	if (s === 0) {
		val = l * 255;
		return [val, val, val];
	}

	if (l < 0.5) {
		t2 = l * (1 + s);
	} else {
		t2 = l + s - l * s;
	}

	const t1 = 2 * l - t2;

	const rgb = [0, 0, 0];
	for (let i = 0; i < 3; i++) {
		t3 = h + 1 / 3 * -(i - 1);
		if (t3 < 0) {
			t3++;
		}

		if (t3 > 1) {
			t3--;
		}

		if (6 * t3 < 1) {
			val = t1 + (t2 - t1) * 6 * t3;
		} else if (2 * t3 < 1) {
			val = t2;
		} else if (3 * t3 < 2) {
			val = t1 + (t2 - t1) * (2 / 3 - t3) * 6;
		} else {
			val = t1;
		}

		rgb[i] = val * 255;
	}

	return rgb;
}

function rgbToHsl (rgb) {
	const r = rgb[0] / 255;
	const g = rgb[1] / 255;
	const b = rgb[2] / 255;
	const min = Math.min(r, g, b);
	const max = Math.max(r, g, b);
	const delta = max - min;
	let h;
	let s;

	if (max === min) {
		h = 0;
	} else if (r === max) {
		h = (g - b) / delta;
	} else if (g === max) {
		h = 2 + (b - r) / delta;
	} else if (b === max) {
		h = 4 + (r - g) / delta;
	}

	h = Math.min(h * 60, 360);

	if (h < 0) {
		h += 360;
	}

	const l = (min + max) / 2;

	if (max === min) {
		s = 0;
	} else if (l <= 0.5) {
		s = delta / (max + min);
	} else {
		s = delta / (2 - max - min);
	}

	return [h, s * 100, l * 100];
}

function lightenDarkenColor(col, percentIncrease) {
  var usePound = false;
  if ( col[0] == "#" ) {
      col = col.slice(1);
      usePound = true;
  }
  var num = parseInt(col,16);

  var r = (num >> 16);

  var g = ((num >> 8) & 0x00FF);

  var b = (num & 0x0000FF);
  let hsl=rgbToHsl([r,g,b]);
  hsl[2]=hsl[2]+percentIncrease
  if(hsl[2]>100)
    hsl[2]=100
  hsl[2]=hsl[2]>94?hsl[2]:94
  let rgb=hslToRgb(hsl)
  return (usePound?"#":"") + (rgb[2] | (rgb[1] << 8) | (rgb[0] << 16)).toString(16);
}

function addBubble(hexColor){
	const bubbleId="bubble"
	removeIfPresent(document,bubbleId)
	const lighterColor=lightenDarkenColor(hexColor,40)
	GM_addStyle("\
		#bubble {\
			position: fixed;\
			top: 0%;\
			transform: translate(-50%, -50%);\
			z-index: 100000000000000000000000000000;\
			border: 2px solid "+lighterColor+";\
			background-color:transparent;\
			padding:15px;\
			border-radius:15px;\
			box-shadow: 2px 2px 5px "+hexColor+", inset -2px -2px 5px "+hexColor+";\
			background-image: radial-gradient(circle, rgba(0,0,0,0.3) 0%, rgba(0,0,0,0) 50%, rgba(0,0,0,0) 100%);\
			cursor: pointer;\
		}\
	")
	const bubble=document.createElement("div")
	bubble.id=bubbleId
  bubble.addEventListener("click",()=>{
    let message=""
    sentinels.forEach(sentinel=>{
      message=message+sentinel.key+": "+GM_getValue(sentinel.key)+"\n"
    })
    message=message+"\nLast updated on: "+GM_getValue(lastUpdateDateTimeId)
    createToast(message,"informational")
  })
	prepend(document.body,bubble)
}

function getFirstElementWithText(elementString, text){
  let result=undefined
  document.querySelectorAll(elementString).forEach(element=>{
    if(!result&& element.innerText===text)result=element
  })
  return result
}

function refreshBubble(){
  let isRed=false
  let isOrange=false
  sentinels.forEach(sentinel=>{
    const storedValue=GM_getValue(sentinel.key)
    isRed=isRed|sentinel.isRed(storedValue)
    isOrange=isOrange|sentinel.isOrange(storedValue)
  })
  if(isRed){
    addBubble(redColor)
  }
  else if(isOrange){
    addBubble(orangeColor)
  }
  else{
    addBubble(noColor)
  }
}

function monitorSite(sentinel){
  GM_xmlhttpRequest({
  url: sentinel.url,
  method: "GET",
  headers:{
    "Access-Control-Allow-Origin": "*",
  },
  responseType: "text/html",
  onload:(response)=>{
    console.log("response: "+JSON.stringify(response))
    const matches=JSON.stringify(response).match(new RegExp(sentinel.regex))
    if(!matches){
      addBubble(redColor)
      GM_setValue(sentinel.key,sentinel.redThreshold-1)
    }
    const currentValue=Number(matches[0])
    GM_setValue(sentinel.key,matches[0])
    refreshBubble()
},
})
}

function monitorSites(){
  const currentDateTime=new Date();
  const lastUpdateDateTime=GM_getValue(lastUpdateDateTimeId)
  if(lastUpdateDateTime&&(currentDateTime.getTime()-new Date(lastUpdateDateTime).getTime())<dataRefreshPeriodMinutes*60*1000){
    refreshBubble()
    return;
  }
  sentinels.forEach(sentinel=>{
    monitorSite(sentinel)
  })
  GM_setValue(lastUpdateDateTimeId,currentDateTime.toISOString())
}

monitorSites()
setInterval(()=>monitorSites(), bubbleRefreshPeriodMinutes*60*1000)





